const int ledRed = 2;       
const int ledYellow = 3;    
const int ledGreen = 4;     
const int btnStop = 5;      
const int btnBack = 6;      
const int btnForward = 7;   
const int dataPin = A0;     
const int clockPin = A2;    
const int latchPin = A1;    
const byte digit[10] = {
  0b11000000,
  0b11111001,
  0b10100100,
  0b10110000,
  0b10011001,
  0b10010010,
  0b10000010,
  0b11111000,
  0b10000000,
  0b10010000,
};
int score = 0;
void setup() {
  Serial.begin(9600);
  pinMode(ledRed, OUTPUT);
  pinMode(ledYellow, OUTPUT);
  pinMode(ledGreen, OUTPUT);
  pinMode(btnStop, INPUT_PULLUP);
  pinMode(btnBack, INPUT_PULLUP);
  pinMode(btnForward, INPUT_PULLUP);
  pinMode(dataPin, OUTPUT);
  pinMode(clockPin, OUTPUT);
  pinMode(latchPin, OUTPUT);
  displayScore(0);
  delay(3000);
  Serial.println("Ready Go!");
}
void displayScore(int num) {
  digitalWrite(latchPin, LOW);
  shiftOut(dataPin, clockPin, MSBFIRST, digit[num]);
  digitalWrite(latchPin, HIGH);
}
void loop() {
  int colors[5];
  colors[0] = 0;
  colors[1] = 1;
  colors[2] = 2;
  colors[3] = random(3);
  colors[4] = random(3);
  for(int i = 0; i < 5; i++) {
    int j = random(5);
    int temp = colors[i];
    colors[i] = colors[j];
    colors[j] = temp;
  }
  for(int i = 0; i < 5; i++) {
    int color = colors[i];
    digitalWrite(ledRed, LOW);
    digitalWrite(ledYellow, LOW);
    digitalWrite(ledGreen, LOW);
    if(color == 0) {
      digitalWrite(ledRed, HIGH);
    } else if(color == 1) {
      digitalWrite(ledYellow, HIGH);
    } else if(color == 2) {
      digitalWrite(ledGreen, HIGH);
    }
    unsigned long startTime = millis();
    bool isCorrect = false;
    int targetButton = -1;
    if(color == 0) {
      targetButton = btnStop;
    } else if(color == 1) {
      targetButton = btnBack;
    } else if(color == 2) {
      targetButton = btnForward;
    }
    while(millis() - startTime < 5000) {
      if(digitalRead(targetButton) == LOW) {
        isCorrect = true;
        break;
      } else {
        if((color != 0 && digitalRead(btnStop) == LOW) ||
           (color != 1 && digitalRead(btnBack) == LOW) ||
           (color != 2 && digitalRead(btnForward) == LOW)) {
          Serial.println("Come On!");
          delay(200);
        }
      }
    }
    digitalWrite(ledRed, LOW);
    digitalWrite(ledYellow, LOW);
    digitalWrite(ledGreen, LOW);
    if(isCorrect) {
      score++;
      Serial.print("Good Job! x");
      Serial.println(score);
      displayScore(score);
      delay(2000);
    } else {
      Serial.println("Time's up!");
      delay(2000);
    }
  }
  digitalWrite(ledRed, LOW);
  digitalWrite(ledYellow, LOW);
  digitalWrite(ledGreen, LOW);
  digitalWrite(latchPin, LOW);
  digitalWrite(latchPin, HIGH);
  Serial.print("Game Over! Your score is ");
  Serial.println(score);
  while(1);
}

