const int buttonPin = 2;    
const int ledPin = 3;        
int buttonState;            
int lastButtonState = HIGH; 
int ledState = HIGH;         
unsigned long lastDebounceTime = 0;  
unsigned long debounceDelay = 50;    
void setup() {
  pinMode(buttonPin, INPUT_PULLUP);  
  pinMode(ledPin, OUTPUT);
  digitalWrite(ledPin, ledState);
}
void loop() {
  int reading = digitalRead(buttonPin);
  if (reading != lastButtonState) {
    lastDebounceTime = millis();
  }
  if ((millis() - lastDebounceTime) > debounceDelay) {
    if (reading != buttonState) {
      buttonState = reading;
      if (buttonState == LOW) {
        ledState = !ledState;  
        digitalWrite(ledPin, ledState);
      }
    }
  }
  lastButtonState = reading;
}

const int redPin = 2;
const int greenPin = 3;
const int bluePin = 4;
const int potPin = A3;  
void setup() {
  pinMode(redPin, OUTPUT);
  pinMode(greenPin, OUTPUT);
  pinMode(bluePin, OUTPUT);
}
void loop() {
  int potValue = analogRead(potPin);
  int third = 1023 / 3; 
  int red = 0;
  int green = 0;
  int blue = 0;
  if (potValue < third) {
    red = map(potValue, 0, third, 255, 255);
    green = 0;
    blue = 0;
  } else if (potValue < 2 * third) {
    red = 0;
    green = map(potValue, third, 2 * third, 255, 255);
    blue = 0;
  } else {
    red = 0;
    green = 0;
    blue = map(potValue, 2 * third, 1023, 255, 255);
  }
  analogWrite(redPin, red);
  analogWrite(greenPin, green);
  analogWrite(bluePin, blue);
}

void setup() {
    pinMode(2, OUTPUT);  
}
void loop() {
    float port1_value = analogRead(A3) / 1023.0;  
    float port2_value = analogRead(A4) / 1023.0;  
    float port3_value = analogRead(A5) / 1023.0;  
    if (port1_value >= port2_value) {
        float led_brightness = port2_value + port3_value * (port1_value - port2_value);
        led_brightness *= 255;  
        analogWrite(2, led_brightness);  
    } else {
        analogWrite(2, 0);  
    }
    delay(10);  
}

