const int buttonPin = 12;  
const int ledPins[10] = {11, 10, 9, 8, 7, 6, 5, 4, 3, 2};
int count = 0;
void setup() {
  pinMode(buttonPin, INPUT_PULLUP);
  for(int i = 0; i < 10; i++) {
    pinMode(ledPins[i], OUTPUT);
  }
}
void loop() {
  static int lastButtonState = HIGH;
  int currentButtonState = digitalRead(buttonPin);
  if (currentButtonState != lastButtonState) {
    if (currentButtonState == LOW) {
      delay(20); 
      if (digitalRead(buttonPin) == LOW) {
        count += 2;
        if (count > 10) {
          count = 0;
        }
        for (int i = 0; i < 10; i++) {
          if (i < count) {
            digitalWrite(ledPins[i], HIGH);
          } else {
            digitalWrite(ledPins[i], LOW);
          }
        }
      }
    }
    lastButtonState = currentButtonState;
  }
}

#include <Arduino.h>
const int potPin = A3;        
const int btnPin = 12;        
const int ledPins[] = {11, 10, 9, 8, 7, 6, 5, 4, 3, 2}; 
int mappedValue = 0;          
bool buttonPressed = false;   
int previousButtonState = HIGH;
void setup() {
  pinMode(btnPin, INPUT_PULLUP);
  for (int i = 0; i < 10; i++) {
    pinMode(ledPins[i], OUTPUT);
  }
}
void loop() {
  int potValue = analogRead(potPin);
  mappedValue = map(potValue, 0, 1023, 0, 10);
  if (mappedValue > 10) mappedValue = 10;
  int currentButtonState = digitalRead(btnPin);
  if (currentButtonState != previousButtonState) {
    currentButtonState = digitalRead(btnPin);
    if (currentButtonState != previousButtonState) {
      buttonPressed = !buttonPressed;
    }
    delay(200); 
  }
  int ledIndex;
  if (buttonPressed) {
    ledIndex = 10 - mappedValue;
  } else {
    ledIndex = mappedValue;
  }
  for (int i = 0; i < 10; i++) {
    if (i < ledIndex) {
      if (buttonPressed) {
        digitalWrite(ledPins[i], LOW); 
      }
      else{
        digitalWrite(ledPins[i], HIGH); 
      }
    } else {
      if (buttonPressed) {
        digitalWrite(ledPins[i], HIGH); 
      }
      else{
        digitalWrite(ledPins[i], LOW); 
      }
    }
  }
  delay(100);
}

void setup() {
  pinMode(A0, OUTPUT);  
  pinMode(A1, OUTPUT);  
  pinMode(A2, OUTPUT);  
}
void loop() {
  display(0); 
  delay(2000); 
  display('A'); 
  delay(2000); 
  display('P'); 
  delay(2000); 
}
void display(char c) {
  byte data = 0;
  switch(c) {
    case 0:
      data = 0b1000000; 
      break;
    case 'A':
      data = 0b0001000; 
      break;
    case 'P':
      data = 0b0001100; 
      break;
  }
  shiftOut(A0, A2, MSBFIRST, data);  
  digitalWrite(A1, HIGH);  
  delayMicroseconds(1);
  digitalWrite(A1, LOW);
  delayMicroseconds(1);
}

