const int dataPin = A0;   
const int clockPin = A2;  
const int latchPin = A1;  
const byte digit[10] = {
  0b11000000,
  0b11111001,
  0b10100100,
  0b10110000,
  0b10011001,
  0b10010010,
  0b10000010,
  0b11111000,
  0b10000000,
  0b10010000,
};
void sendScore(uint8_t high, uint8_t low) {
  digitalWrite(latchPin, LOW);
  shiftOut(dataPin, clockPin, MSBFIRST, low);
  shiftOut(dataPin, clockPin, MSBFIRST, high);
  digitalWrite(latchPin, HIGH);
}
unsigned long previousMillis = -1000;
const unsigned long interval = 1000; 
int seconds = -6;
void setup() {
  pinMode(dataPin, OUTPUT);
  pinMode(clockPin, OUTPUT);
  pinMode(latchPin, OUTPUT);
}
void loop() {
  unsigned long currentMillis = millis();
  if(currentMillis - previousMillis >= interval) {
    previousMillis = currentMillis;
    seconds += 6;
    int high = seconds % 100 / 10;
    int low = seconds % 10;
    sendScore(high ? digit[high] : 0xff, digit[low]);
  }
}

const int SHCP_PIN = A2;  
const int STCP_PIN = A1;  
const int DS_PIN = A0;    
const int BUTTON_PIN = 2;
const byte segmentCodes[] = {
  0b11000000,
  0b11111001,
  0b10100100,
  0b10110000,
  0b10011001,
  0b10010010,
  0b10000010,
  0b11111000,
  0b10000000,
  0b10010000,
};
int currentNumber = 0;
void setup() {
  Serial.begin(9600);
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  pinMode(BUTTON_PIN, INPUT_PULLUP); 
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, segmentCodes[0]); 
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, segmentCodes[0]);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}
void loop() {
  if (digitalRead(BUTTON_PIN) == LOW) {
    delay(20); 
    if (digitalRead(BUTTON_PIN) == LOW) {
      currentNumber += 37;
      if (currentNumber > 99) {
        currentNumber = 0;
      }
      displayNumber(currentNumber);
      while (digitalRead(BUTTON_PIN) == LOW); 
    }
  }
}
void displayNumber(int number) {
  int tens = number / 10;
  int ones = number % 10;
  byte tensCode = segmentCodes[tens];
  byte onesCode = segmentCodes[ones];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, onesCode);
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, tensCode);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}

