  int potValue = analogRead(POT_PIN);
  int colorRange = potValue / 128;  
  digitalWrite(R_PIN, LOW);
  digitalWrite(G_PIN, LOW);
  digitalWrite(B_PIN, LOW);
  switch(colorRange) {
    case 1:  
      digitalWrite(R_PIN, HIGH);
      break;
    case 2:  
      digitalWrite(G_PIN, HIGH);
      break;
    case 3:  
      digitalWrite(B_PIN, HIGH);
      break;
    case 4:  
      digitalWrite(R_PIN, HIGH);
      digitalWrite(G_PIN, HIGH);
      break;
    case 5:  
      digitalWrite(R_PIN, HIGH);
      digitalWrite(B_PIN, HIGH);
      break;
    case 6:  
      digitalWrite(G_PIN, HIGH);
      digitalWrite(B_PIN, HIGH);
      break;
    case 7:  
      digitalWrite(R_PIN, HIGH);
      digitalWrite(G_PIN, HIGH);
      digitalWrite(B_PIN, HIGH);
      break;
  }
  delay(10);
}

