    pinMode(pin, OUTPUT);  
    digitalWrite(pin, LOW);  
  }
}
void loop() {
  int pot1Value = analogRead(A3);  
  int pot2Value = analogRead(A4);  
  int pot3Value = analogRead(A5);  
  int ledsSection1 = map(pot1Value, 0, 1023, 0, 3);
  int ledsSection2 = map(pot2Value, 0, 1023, 0, 3);
  int ledsSection3 = map(pot3Value, 0, 1023, 0, 4);
  updateSection(2, 4, ledsSection1);  
  updateSection(5, 7, ledsSection2);  
  updateSection(8, 11, ledsSection3);  
}
void updateSection(int startPin, int endPin, int activeLEDs) {
  for (int pin = startPin; pin <= endPin; pin++) {
    bool state = (pin - startPin) < activeLEDs;
    digitalWrite(pin, state ? HIGH : LOW);
  }
}

void setup() {
  for (int pin = 2; pin < 12; pin++) {
    pinMode(pin, OUTPUT);  
  }
}
void loop() {
  int lower = map(analogRead(A3), 0, 1023, 2, 11);       
  int upper = map(analogRead(A4), 0, 1023, 2, 11);       
  int fillPercent = map(analogRead(A5), 0, 1023, 0, 100); 
  int start = min(lower, upper);
  int end = max(lower, upper);
  int numLEDs = end - start + 1;
  int fillCount = round((fillPercent / 100.0) * numLEDs); 
  for (int led = 2; led < 12; led++) {
    bool isActive = false;
    if (led >= start && led <= end) {
      int position = (lower <= upper) ? (led - start) : (end - led);
      isActive = (position < fillCount);
    }
    digitalWrite(led, isActive ? HIGH : LOW); 
  }
  delay(20); 
}

