  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000  
};
int currentNumber = 0;
void setup() {
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  servo1.attach(2);  
  servo2.attach(3);  
  servo3.attach(4);  
  servo1.write(0);
  servo2.write(0);
  servo3.write(0);
  displayNumber(currentNumber);
}
void loop() {
  for (int i = 0; i <= 9; i++) {
