  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, code);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}
void moveServos(int number) {
  if (number >= 0 && number <= 2) {
    servo1.write(0); 
    servo2.write(45);
    servo3.write(90);
  } else if (number >= 3 && number <= 5) {
    servo1.write(45);
    servo2.write(90);
    servo3.write(135);
  } else if (number >= 6 && number <= 8) {
    servo1.write(90);
    servo2.write(135);
    servo3.write(180);
  } else if (number == 9) {
    servo1.write(0);
    servo2.write(0);
    servo3.write(0);
  }
}

