const int interval = 2000;
void setup() {
  for (int pin = 2; pin <= 11; pin++) {
    pinMode(pin, OUTPUT);
  }
  pinMode(DS_PIN, OUTPUT);
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  updateDisplays(0); 
