void hsvToRgb(float h, float s, float v, int &r, int &g, int &b) {
  int i = int(h / 60) % 6;
  float f = (h / 60) - i;
  float p = v * (1 - s);
  float q = v * (1 - f * s);
  float t = v * (1 - (1 - f) * s);
  switch (i) {
    case 0: r = v * 255; g = t * 255; b = p * 255; break;
    case 1: r = q * 255; g = v * 255; b = p * 255; break;
    case 2: r = p * 255; g = v * 255; b = t * 255; break;
    case 3: r = p * 255; g = q * 255; b = v * 255; break;
    case 4: r = t * 255; g = p * 255; b = v * 255; break;
    case 5: r = v * 255; g = p * 255; b = q * 255; break;
  }
}
void setup() {
  pinMode(2, OUTPUT);  
  pinMode(3, OUTPUT);  
  pinMode(4, OUTPUT);  
  pinMode(5, OUTPUT);  
  pinMode(6, OUTPUT);  
  pinMode(7, OUTPUT);  
}
void loop() {
  int pot1Value = analogRead(A3);  
  int pot2Value = analogRead(A4);  
  float hue1 = map(pot1Value, 0, 1023, 0, 360);
  float hue2 = map(pot2Value, 0, 1023, 0, 360);
  int r1, g1, b1;
  hsvToRgb(hue1, 1.0, 1.0, r1, g1, b1);
  analogWrite(2, r1);  
  analogWrite(3, g1);  
  analogWrite(4, b1);  
  int r2, g2, b2;
  hsvToRgb(hue2, 1.0, 1.0, r2, g2, b2);
  analogWrite(5, r2);  
  analogWrite(6, g2);  
  analogWrite(7, b2);  
}

