    pinMode(pin, OUTPUT);
  }
  for (int pin = 10; pin <= 13; pin++) {  
    pinMode(pin, OUTPUT);
  }
  pinMode(44, OUTPUT);  
  pinMode(45, OUTPUT);  
}
void loop() {
  static int angle = 0;
  servo1.write(angle);
  servo2.write(angle);
  analogWrite(2, 255);  
  analogWrite(3, 0);    
  analogWrite(4, 0);    
  if (angle > 90){
    analogWrite(5, 0);  
    analogWrite(6, 0);    
    analogWrite(7, 255);    
  }else{
    analogWrite(5, 0);    
    analogWrite(6, 0);    
    analogWrite(7, 0);    
  }
  int litLEDs = angle / 30;
  int pins[] = {10, 11, 12, 13, 44, 45};
  for (int i = 0; i < 6; i++) {
      digitalWrite(pins[i], (i < litLEDs) ? HIGH : LOW);
  }
  delay(2000);
  angle = (angle >= 180) ? 0 : angle + 30;
}

#include <Servo.h>
Servo servo1;
Servo servo2;
Servo servo3;
const int servo1Pin = 5;  
const int servo2Pin = 6;  
const int servo3Pin = 7;  
const int redPin = 2;    
const int greenPin = 3;   
const int bluePin = 4;    
const int barPins[] = {8, 9, 10, 11, 12, 13, 44, 45};  
const int numLEDs = 8;
int currentServoIndex = 0;
int servoAngles[] = {0, 0, 0};
unsigned long previousMillis = 0;
const long interval = 2000;
void setup() {
  servo1.attach(servo1Pin);
  servo2.attach(servo2Pin);
  servo3.attach(servo3Pin);
  servo1.write(0);
  servo2.write(0);
  servo3.write(0);
  pinMode(redPin, OUTPUT);
  pinMode(greenPin, OUTPUT);
  pinMode(bluePin, OUTPUT);
  analogWrite(redPin, 0);
  analogWrite(greenPin, 0);
  analogWrite(bluePin, 0);
  for (int i = 0; i < numLEDs; i++) {
    pinMode(barPins[i], OUTPUT);
    digitalWrite(barPins[i], LOW);
  }
}
void loop() {
  unsigned long currentMillis = millis();
  if (currentMillis - previousMillis >= interval) {
    previousMillis = currentMillis;
    servoAngles[currentServoIndex] = (servoAngles[currentServoIndex] == 0) ? 180 : 0;
    switch (currentServoIndex) {
      case 0: servo1.write(servoAngles[0]); break;
      case 1: servo2.write(servoAngles[1]); break;
      case 2: servo3.write(servoAngles[2]); break;
    }
    currentServoIndex = (currentServoIndex + 1) % 3;
  }
  int r = map(servoAngles[0], 0, 180, 0, 255);
  int g = map(servoAngles[1], 0, 180, 0, 255);
  int b = map(servoAngles[2], 0, 180, 0, 255);
  analogWrite(redPin, r);
  analogWrite(greenPin, g);
  analogWrite(bluePin, b);
  int avg = (r + g + b) / 3;
  int ledCount = map(avg, 0, 255, 0, 8);
  ledCount = constrain(ledCount, 0, 8);
  for (int i = 0; i < numLEDs; i++) {
    digitalWrite(barPins[i], (i < ledCount) ? HIGH : LOW);
  }
}

