}
void loop() {
  if (millis() - lastModeChange >= 5000) {
    colorMode = (colorMode + 1) % 3;
    lastModeChange = millis();
  }
  int potValue = analogRead(A3); 
  updateColorIntensity(potValue);
  analogWrite(RGB_PINS[0], redIntensity);
  analogWrite(RGB_PINS[1], greenIntensity);
  analogWrite(RGB_PINS[2], blueIntensity);
  int segments = map(potValue, 0, 1023, 0, 10);
  for (int i = 0; i < 10; i++) {
    digitalWrite(BAR_PINS[i], (i < segments) ? HIGH : LOW);
  }
}
void updateColorIntensity(int value) {
  switch(colorMode) {
    case 0: 
      redIntensity = map(value, 0, 1023, 0, 255);
      break;
    case 1: 
      greenIntensity = map(value, 0, 1023, 0, 255);
      break;
    case 2: 
      blueIntensity = map(value, 0, 1023, 0, 255);
      break;
  }
}

const int pot1Pin = A3;  
const int pot2Pin = A4;  
const int rgb1Pins[] = {2, 3, 4};  
const int rgb2Pins[] = {5, 6, 7};  
void setup() {
  for (int pin : rgb1Pins) pinMode(pin, OUTPUT);
  for (int pin : rgb2Pins) pinMode(pin, OUTPUT);
  for (int pin = 8; pin <= 13; pin++) pinMode(pin, OUTPUT);  
  pinMode(44, OUTPUT);  
  pinMode(45, OUTPUT);  
}
void loop() {
  int pot1Value = analogRead(pot1Pin);
  int pot2Value = analogRead(pot2Pin);
  updateRGB(pot1Value, rgb1Pins[0], rgb1Pins[1], rgb1Pins[2]);
  updateRGB(pot2Value, rgb2Pins[0], rgb2Pins[1], rgb2Pins[2]);
  int average = (pot1Value + pot2Value) / 2;
  int numLEDs = map(average, 0, 1023, 0, 9);
  int pins[] = {8, 9, 10, 11, 12, 13, 44, 45};
  for (int i = 0; i < 8; i++) {
    digitalWrite(pins[i], i < numLEDs ? HIGH : LOW);
  }
  digitalWrite(44, 8 < numLEDs ? HIGH : LOW);  
  digitalWrite(45, 9 < numLEDs ? HIGH : LOW);  
  delay(10);
}
void updateRGB(int value, int rPin, int gPin, int bPin) {
  digitalWrite(rPin, LOW);
  digitalWrite(gPin, LOW);
  digitalWrite(bPin, LOW);
  if (value < 341) digitalWrite(rPin, HIGH);
  else if (value < 682) digitalWrite(gPin, HIGH);
  else digitalWrite(bPin, HIGH);
}

