  for(int p = 2; p <=7; p++) digitalWrite(p, LOW);
  if(val <= 5) {        
    digitalWrite(2, HIGH);
    digitalWrite(5, HIGH);
  } else if(val <=  9) { 
    digitalWrite(3, HIGH);
    digitalWrite(6, HIGH);
  } else {               
    digitalWrite(4, HIGH);
    digitalWrite(7, HIGH);
  }
}
