    delay(100); 
    if (digitalRead(btnPin) == LOW) {
      currentColor = (currentColor + direction + 8) % 8;
      updateLED();
      while (digitalRead(btnPin) == LOW); 
    }
  }
}
void updateLED() {
  digitalWrite(R_PIN, colors[currentColor][0]);
  digitalWrite(G_PIN, colors[currentColor][1]);
  digitalWrite(B_PIN, colors[currentColor][2]);
}

const int BUTTON_PIN = 2;  
const int RED_PIN = 3;     
const int GREEN_PIN = 4;   
const int BLUE_PIN = 5;    
int colorState = -1;  
unsigned long lastChangeTime = 0;  
bool buttonActive = false;  
void setup() {  
  pinMode(BUTTON_PIN, INPUT_PULLUP);  
  pinMode(RED_PIN, OUTPUT);  
  pinMode(GREEN_PIN, OUTPUT);  
  pinMode(BLUE_PIN, OUTPUT);  
  analogWrite(RED_PIN, 0);  
  analogWrite(GREEN_PIN, 0);  
  analogWrite(BLUE_PIN, 0);  
}  
void loop() {  
  if (digitalRead(BUTTON_PIN) == LOW && !buttonActive) {  
    delay(100);  
    if (digitalRead(BUTTON_PIN) == LOW) {  
      buttonActive = true;  
      colorState = (colorState + 1) % 8;  
      lastChangeTime = millis();  
      updateLED();  
    }  
  }  
  if (digitalRead(BUTTON_PIN) == HIGH) {  
    buttonActive = false;  
  }  
}  
void updateLED() {  
  switch(colorState) {  
    case 0:  
      analogWrite(RED_PIN, 255);  
      analogWrite(GREEN_PIN, 0);  
      analogWrite(BLUE_PIN, 0);  
      break;  
    case 1:  
      analogWrite(RED_PIN, 0);  
      analogWrite(GREEN_PIN, 255);  
      analogWrite(BLUE_PIN, 0);  
      break;  
    case 2:  
      analogWrite(RED_PIN, 0);  
      analogWrite(GREEN_PIN, 0);  
      analogWrite(BLUE_PIN, 255);  
      break;  
    case 3:  
      analogWrite(RED_PIN, 255);  
      analogWrite(GREEN_PIN, 255);  
      analogWrite(BLUE_PIN, 0);  
      break;  
    case 4:  
      analogWrite(RED_PIN, 0);  
      analogWrite(GREEN_PIN, 255);  
      analogWrite(BLUE_PIN, 255);  
      break;  
    case 5:  
      analogWrite(RED_PIN, 255);  
      analogWrite(GREEN_PIN, 0);  
      analogWrite(BLUE_PIN, 255);  
      break;  
    case 6:  
      analogWrite(RED_PIN, 255);  
      analogWrite(GREEN_PIN, 255);  
      analogWrite(BLUE_PIN, 255);  
      break;  
    case 7:  
      analogWrite(RED_PIN, 0);  
      analogWrite(GREEN_PIN, 0);  
      analogWrite(BLUE_PIN, 0);  
      break;  
  }  
}

#include <Servo.h>
Servo servo1;
Servo servo2;
Servo servo3;
const int BUTTON_PIN = 2; 
int currentState = 0;     
unsigned long lastDebounceTime = 0; 
unsigned long debounceDelay = 150; 
bool buttonPressed = false; 
void setup() {
  servo1.attach(3); 
  servo2.attach(4); 
  servo3.attach(5); 
  pinMode(BUTTON_PIN, INPUT_PULLUP); 
  setServoAngles(0, 0, 0); 
}
void loop() {
  int buttonState = digitalRead(BUTTON_PIN); 
  if (buttonState == LOW && !buttonPressed && (millis() - lastDebounceTime) > debounceDelay) {
    buttonPressed = true; 
    lastDebounceTime = millis(); 
    currentState = (currentState + 1) % 5;
    switch (currentState) {
      case 0:
        setServoAngles(0, 0, 0);
        break;
      case 1:
        setServoAngles(90, 45, 135);
        break;
      case 2:
        setServoAngles(180, 90, 0);
        break;
      case 3:
        setServoAngles(45, 135, 90);
        break;
      case 4:
        setServoAngles(0, 0, 0);
        break;
    }
  }
  if (buttonState == HIGH && buttonPressed && (millis() - lastDebounceTime) > debounceDelay) {
    buttonPressed = false; 
    lastDebounceTime = millis(); 
  }
}
void setServoAngles(int angle1, int angle2, int angle3) {
  servo1.write(angle1);
  servo2.write(angle2);
  servo3.write(angle3);
}

#include <Servo.h>
Servo servo1;
Servo servo2;
const int BUTTON_PIN = 2;  
const int SERVO1_PIN = 3;  
const int SERVO2_PIN = 4;  
int servo1Pos = 0;         
int servo2Pos = 180;       
bool buttonState = HIGH;   
bool lastButtonState = HIGH; 
unsigned long lastDebounceTime = 0; 
const unsigned long debounceDelay = 150; 
void setup() {
  pinMode(BUTTON_PIN, INPUT_PULLUP);
  servo1.attach(SERVO1_PIN);
  servo2.attach(SERVO2_PIN);
  servo1.write(servo1Pos);
  servo2.write(servo2Pos);
}
void loop() {
  int reading = digitalRead(BUTTON_PIN);
  if (reading != lastButtonState) {
    lastDebounceTime = millis();
  }
  if ((millis() - lastDebounceTime) > debounceDelay) {
    if (reading != buttonState) {
      buttonState = reading;
      if (buttonState == LOW) {
        toggleServoPositions();
      }
    }
  }
  lastButtonState = reading;
}
void toggleServoPositions() {
  if (servo1Pos == 0 && servo2Pos == 180) {
    servo1Pos = 180;
    servo2Pos = 0;
  } else {
    servo1Pos = 0;
    servo2Pos = 180;
  }
  servo1.write(servo1Pos);
  servo2.write(servo2Pos);
  delay(200);  
}

const int BTN_RED = 2;    
const int BTN_GREEN = 3;  
const int BTN_BLUE = 4;   
const int POT_RED = A3;   
const int POT_GREEN = A4; 
const int POT_BLUE = A5;  
const int LED_RED = 5;  
const int LED_GREEN = 6;  
const int LED_BLUE = 7;  
int red = 0;  
int green = 0;  
int blue = 0;  
void setup() {  
  pinMode(BTN_RED, INPUT_PULLUP);  
  pinMode(BTN_GREEN, INPUT_PULLUP);  
  pinMode(BTN_BLUE, INPUT_PULLUP);  
  pinMode(LED_RED, OUTPUT);  
  pinMode(LED_GREEN, OUTPUT);  
  pinMode(LED_BLUE, OUTPUT);  
}  
void loop() {  
  checkButton(BTN_RED, POT_RED, &red);  
  checkButton(BTN_GREEN, POT_GREEN, &green);  
  checkButton(BTN_BLUE, POT_BLUE, &blue);  
  updateLED();  
}  
void checkButton(int buttonPin, int potPin, int *color) {  
  if (digitalRead(buttonPin) == LOW) {  
    delay(100);  
    if (digitalRead(buttonPin) == LOW) {  
      while (digitalRead(buttonPin) == LOW) {  
        *color = map(analogRead(potPin), 0, 1023, 0, 255);  
        updateLED();  
        delay(10);  
      }  
    }  
  }  
}  
void updateLED() {  
  analogWrite(LED_RED, red);  
  analogWrite(LED_GREEN, green);  
  analogWrite(LED_BLUE, blue);  
}

const int BUTTON_PIN = 2;  
const int POT_PIN = A3;   
const int RED_PIN = 3;    
const int GREEN_PIN = 4;  
const int BLUE_PIN = 5;   
int currentMode = -1; 
int lastButtonState = HIGH;
int currentButtonState = HIGH;
unsigned long lastDebounceTime = 0;
const int DEBOUNCE_DELAY = 50; 
void setup() {
  pinMode(BUTTON_PIN, INPUT_PULLUP);
  pinMode(RED_PIN, OUTPUT);
  pinMode(GREEN_PIN, OUTPUT);
  pinMode(BLUE_PIN, OUTPUT);
  analogWrite(RED_PIN, 0);
  analogWrite(GREEN_PIN, 0);
  analogWrite(BLUE_PIN, 0);
}
void loop() {
  int reading = digitalRead(BUTTON_PIN);
  if (reading != lastButtonState) {
    lastDebounceTime = millis(); 
  }
  if ((millis() - lastDebounceTime) > DEBOUNCE_DELAY) {
    if (reading != currentButtonState) {
      currentButtonState = reading;
      if (currentButtonState == LOW) {
        if (currentMode == -1) {
          currentMode = 0; 
        } else {
          currentMode = (currentMode + 1) % 3; 
        }
      }
    }
  }
  lastButtonState = reading; 
  int brightness = map(analogRead(POT_PIN), 0, 1023, 0, 255);
  switch (currentMode) {
    case -1: 
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, 0);
      break;
    case 0: 
      analogWrite(RED_PIN, brightness);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, 0);
      break;
    case 1: 
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, brightness);
      analogWrite(BLUE_PIN, 0);
      break;
    case 2: 
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, brightness);
      break;
  }
}

