const int SHCP_PIN = A2;  
const int STCP_PIN = A1;  
const int DS_PIN = A0;    
const int BUTTON1_PIN = 2;  
const int BUTTON2_PIN = 3;  
const int POT1_PIN = A3;    
const int POT2_PIN = A4;    
const byte segmentCodes[] = {
  0b11000000, 
  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000, 
};
int currentNumber = 0;
unsigned long lastIncrementTime = 0;
unsigned long lastDecrementTime = 0;
void setup() {
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  pinMode(BUTTON1_PIN, INPUT_PULLUP);
  pinMode(BUTTON2_PIN, INPUT_PULLUP);
  currentNumber = (analogRead(POT1_PIN) / 102.3) * 10 + (analogRead(POT2_PIN) / 102.3);
  displayNumber(currentNumber);
}
void loop() {
  int pot1Value = analogRead(POT1_PIN);
  int pot2Value = analogRead(POT2_PIN);
  int incrementSpeed = map(pot1Value, 0, 1023, 100, 1000); 
  int decrementSpeed = map(pot2Value, 0, 1023, 100, 1000); 
  if (digitalRead(BUTTON1_PIN) == LOW) {
    if (millis() - lastIncrementTime > incrementSpeed) {
      currentNumber++;
      if (currentNumber > 99) {
        currentNumber = 0;
      }
      displayNumber(currentNumber);
      lastIncrementTime = millis();
    }
  }
  if (digitalRead(BUTTON2_PIN) == LOW) {
    if (millis() - lastDecrementTime > decrementSpeed) {
      currentNumber--;
      if (currentNumber < 0) {
        currentNumber = 99;
      }
      displayNumber(currentNumber);
      lastDecrementTime = millis();
    }
  }
}
void displayNumber(int number) {
  int tens = number / 10;
  int ones = number % 10;
  byte tensCode = segmentCodes[tens];
  byte onesCode = segmentCodes[ones];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, onesCode);
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, tensCode);
  digitalWrite(STCP_PIN, HIGH);
