const int RED_PIN = 4;      
const int GREEN_PIN = 5;   
const int BLUE_PIN = 6;    
const unsigned long DEBOUNCE_TIME = 50;
int colorMode = 0;  
unsigned long lastColorChangeTime = 0;  
int button1State = HIGH;
int lastButton1State = HIGH;
unsigned long lastButton1DebounceTime = 0;
int button2State = HIGH;
int lastButton2State = HIGH;
unsigned long lastButton2DebounceTime = 0;
bool button1Pressed = false;
bool button2Pressed = false;
void setup() {
  pinMode(BUTTON1_PIN, INPUT_PULLUP);
  pinMode(BUTTON2_PIN, INPUT_PULLUP);
  pinMode(RED_PIN, OUTPUT);
  pinMode(GREEN_PIN, OUTPUT);
  pinMode(BLUE_PIN, OUTPUT);
}
void loop() {
  int reading1 = digitalRead(BUTTON1_PIN);
  int reading2 = digitalRead(BUTTON2_PIN);
  if (reading1 != lastButton1State) {
    lastButton1DebounceTime = millis();
  }
  if ((millis() - lastButton1DebounceTime) > DEBOUNCE_TIME) {
    if (reading1 != button1State) {
      button1State = reading1;
      if (button1State == LOW) {
        handleButton1Press();
      }
    }
  }
  if (reading2 != lastButton2State) {
    lastButton2DebounceTime = millis();
  }
  if ((millis() - lastButton2DebounceTime) > DEBOUNCE_TIME) {
    if (reading2 != button2State) {
      button2State = reading2;
      if (button2State == LOW) {
        handleButton2Press();
      }
    }
  }
  lastButton1State = reading1;
  lastButton2State = reading2;
  int brightness = analogRead(POT_PIN) / 4;  
  setColorMode(brightness);
  if (button1Pressed && button2Pressed) {
    resetState();
    button1Pressed = false;
    button2Pressed = false;
  }
}
void handleButton1Press() {
  button1Pressed = true;
  if (button2Pressed) {
    return;
  }
  if (colorMode < 2) {
    colorMode++;
  } else {
    colorMode = 0;
  }
  lastColorChangeTime = millis();
}
void handleButton2Press() {
  button2Pressed = true;
  if (button1Pressed) {
    return;
  }
  if (colorMode >= 3 && colorMode < 5) {
    colorMode++;
  } else {
    colorMode = 3;
  }
  lastColorChangeTime = millis();
}
void setColorMode(int brightness) {
  switch (colorMode) {
    case 0:  
      analogWrite(RED_PIN, brightness);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, 0);
      break;
    case 1:  
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, brightness);
      analogWrite(BLUE_PIN, 0);
      break;
    case 2:  
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, brightness);
      break;
    case 3:  
      analogWrite(RED_PIN, brightness);
      analogWrite(GREEN_PIN, brightness);
      analogWrite(BLUE_PIN, 0);
      break;
    case 4:  
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, brightness);
      analogWrite(BLUE_PIN, brightness);
      break;
    case 5:  
      analogWrite(RED_PIN, brightness);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, brightness);
      break;
    case 6:  
      analogWrite(RED_PIN, 0);
      analogWrite(GREEN_PIN, 0);
      analogWrite(BLUE_PIN, 0);
      break;
  }
}
void resetState() {
  colorMode = 6;
  setColorMode(0);
}

const int BUTTON_PIN = 2;  
int currentMode = 0;
unsigned long lastDebounceTime = 0;
bool buttonState = HIGH;  
bool lastButtonState = HIGH;  
void setup() {
  pinMode(BUTTON_PIN, INPUT_PULLUP);
  for (int i = 3; i <= 8; i++) {  
    pinMode(i, OUTPUT);
  }
  for (int i = 9; i <= 13; i++) {  
    pinMode(i, OUTPUT);
  }
  pinMode(44, OUTPUT);  
  pinMode(45, OUTPUT);  
  updateDisplay();
}
void loop() {
  int reading = digitalRead(BUTTON_PIN);
  if (reading != lastButtonState) {
    lastDebounceTime = millis();  
  }
  if ((millis() - lastDebounceTime) > 50) {
    if (reading != buttonState) {
      buttonState = reading;
      if (buttonState == LOW) {
        currentMode = (currentMode + 1) % 8;
        updateDisplay();
      }
    }
  }
  lastButtonState = reading;  
}
void updateDisplay() {
  for (int i = 3; i <= 13; i++) {
    digitalWrite(i, LOW);
  }
  digitalWrite(44, LOW);
  digitalWrite(45, LOW);
  switch (currentMode) {
    case 1:
      digitalWrite(3, HIGH);  
      digitalWrite(8, HIGH);  
      digitalWrite(9, HIGH);  
      break;
    case 2:
      digitalWrite(4, HIGH);  
      digitalWrite(7, HIGH);  
      digitalWrite(8, HIGH);  
      digitalWrite(9, HIGH);  
      digitalWrite(10, HIGH);  
      break;
    case 3:
      digitalWrite(3, HIGH);  
      digitalWrite(4, HIGH);  
      digitalWrite(6, HIGH);  
      digitalWrite(8, HIGH);  
      for (int i = 9; i <= 11; i++) digitalWrite(i, HIGH);  
      break;
    case 4:
      digitalWrite(4, HIGH);  
      digitalWrite(5, HIGH);  
      digitalWrite(6, HIGH);  
      digitalWrite(7, HIGH);  
      for (int i = 9; i <= 12; i++) digitalWrite(i, HIGH);  
      break;
    case 5:
      digitalWrite(3, HIGH);  
      digitalWrite(5, HIGH);  
      digitalWrite(7, HIGH);  
      digitalWrite(8, HIGH);  
      for (int i = 9; i <= 13; i++) digitalWrite(i, HIGH);  
      break;
    case 6:
      digitalWrite(3, HIGH);  
      digitalWrite(4, HIGH);  
      digitalWrite(5, HIGH);  
      digitalWrite(6, HIGH);  
      digitalWrite(7, HIGH);  
      for (int i = 9; i <= 13; i++) digitalWrite(i, HIGH);  
      digitalWrite(44, HIGH);
      break;
    case 7:
      digitalWrite(6, HIGH);  
      digitalWrite(7, HIGH);  
      digitalWrite(8, HIGH);  
      for (int i = 9; i <= 13; i++) digitalWrite(i, HIGH);  
      digitalWrite(44, HIGH);  
      digitalWrite(45, HIGH);  
      break;
  }
}

