void checkButton(int buttonPin, int* color) {
  if (digitalRead(buttonPin) == LOW) {
    delay(50); 
    if (digitalRead(buttonPin) == LOW) {
      *color = (*color + 32) % 256; 
      updateBarGraph(*color / 32); 
      while (digitalRead(buttonPin) == LOW); 
    }
  }
}
void updateBarGraph(int level) {
  for (int i = 8; i <= 13; i++) {
