const int RGB2_RED_PIN = 8; 
const int RGB2_GREEN_PIN = 9; 
const int RGB2_BLUE_PIN = 10; 
const byte segmentCodes[] = {
  0b11000000, 
  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000, 
};
int count = 0;
unsigned long lastDebounceTime1 = 0; 
unsigned long lastDebounceTime2 = 0; 
unsigned long lastDebounceTime3 = 0; 
const unsigned long debounceDelay = 50; 
bool button1State = HIGH; 
bool button2State = HIGH; 
bool button3State = HIGH; 
bool lastButton1State = HIGH; 
bool lastButton2State = HIGH; 
bool lastButton3State = HIGH; 
void setup() {
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  pinMode(BUTTON1_PIN, INPUT_PULLUP);
  pinMode(BUTTON2_PIN, INPUT_PULLUP);
  pinMode(BUTTON3_PIN, INPUT_PULLUP);
  pinMode(RGB1_RED_PIN, OUTPUT);
  pinMode(RGB1_GREEN_PIN, OUTPUT);
  pinMode(RGB1_BLUE_PIN, OUTPUT);
  pinMode(RGB2_RED_PIN, OUTPUT);
  pinMode(RGB2_GREEN_PIN, OUTPUT);
  pinMode(RGB2_BLUE_PIN, OUTPUT);
  displayNumber(0);
  updateRGBLEDs();
}
void loop() {
  int reading1 = digitalRead(BUTTON1_PIN); 
  int reading2 = digitalRead(BUTTON2_PIN); 
  int reading3 = digitalRead(BUTTON3_PIN); 
  if (reading1 != lastButton1State) {
    lastDebounceTime1 = millis(); 
  }
  if ((millis() - lastDebounceTime1) > debounceDelay) {
    if (reading1 != button1State) {
      button1State = reading1;
      if (button1State == LOW) { 
        count++;
        if (count > 15) {
          count = 0;
        }
        displayNumber(count);
        updateRGBLEDs();
      }
    }
  }
  lastButton1State = reading1; 
  if (reading2 != lastButton2State) {
    lastDebounceTime2 = millis(); 
  }
  if ((millis() - lastDebounceTime2) > debounceDelay) {
    if (reading2 != button2State) {
      button2State = reading2;
      if (button2State == LOW) { 
        count++;
        if (count > 15) {
          count = 0;
        }
        displayNumber(count);
        updateRGBLEDs();
      }
    }
  }
  lastButton2State = reading2; 
  if (reading3 != lastButton3State) {
    lastDebounceTime3 = millis(); 
  }
  if ((millis() - lastDebounceTime3) > debounceDelay) {
    if (reading3 != button3State) {
      button3State = reading3;
      if (button3State == LOW) { 
        count++;
        if (count > 15) {
          count = 0;
        }
        displayNumber(count);
        updateRGBLEDs();
      }
    }
  }
  lastButton3State = reading3; 
}
void displayNumber(int number) {
  byte code = segmentCodes[number % 10];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, code);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}
void updateRGBLEDs() {
  if (count < 5) {
    analogWrite(RGB1_RED_PIN, 255);
    analogWrite(RGB1_GREEN_PIN, 0);
    analogWrite(RGB1_BLUE_PIN, 0);
    analogWrite(RGB2_RED_PIN, 0);
    analogWrite(RGB2_GREEN_PIN, 0);
    analogWrite(RGB2_BLUE_PIN, 255);
  } else if (count >= 5 && count < 10) {
    analogWrite(RGB1_RED_PIN, 0);
    analogWrite(RGB1_GREEN_PIN, 255);
    analogWrite(RGB1_BLUE_PIN, 0);
    analogWrite(RGB2_RED_PIN, 255);
    analogWrite(RGB2_GREEN_PIN, 255);
    analogWrite(RGB2_BLUE_PIN, 0);
  } else if (count >= 10) {
    analogWrite(RGB1_RED_PIN, 255);
    analogWrite(RGB1_GREEN_PIN, 0);
    analogWrite(RGB1_BLUE_PIN, 255);
    analogWrite(RGB2_RED_PIN, 0);
    analogWrite(RGB2_GREEN_PIN, 255);
    analogWrite(RGB2_BLUE_PIN, 255);
  }
}

