#include <Servo.h>
const int LED1_PIN = 2;  
const int LED2_PIN = 3;  
const int LED3_PIN = 4;  
const int SHCP_PIN = A2; 
const int STCP_PIN = A1; 
const int DS_PIN = A0;   
const int SERVO1_PIN = 5; 
const int SERVO2_PIN = 6; 
const int SERVO3_PIN = 7; 
Servo servo1;
Servo servo2;
Servo servo3;
int currentNumber = 0;
unsigned long lastUpdateTime = 0;
const byte segmentCodes[] = {
  0b11000000, 
  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000  
};
void setup() {
  pinMode(LED1_PIN, OUTPUT);
  pinMode(LED2_PIN, OUTPUT);
  pinMode(LED3_PIN, OUTPUT);
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  servo1.attach(SERVO1_PIN);
  servo2.attach(SERVO2_PIN);
  servo3.attach(SERVO3_PIN);
  displayNumber(currentNumber);
  updateLEDs(currentNumber);
  updateServos(currentNumber);
}
void loop() {
  if (millis() - lastUpdateTime >= 2000) {
    currentNumber++;
    if (currentNumber > 99) {
      currentNumber = 0;
    }
    displayNumber(currentNumber);
    updateLEDs(currentNumber);
    updateServos(currentNumber);
    lastUpdateTime = millis();
  }
}
void displayNumber(int number) {
  int tens = number / 10;
  int ones = number % 10;
  byte tensCode = segmentCodes[tens];
  byte onesCode = segmentCodes[ones];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, onesCode);
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, tensCode);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}
void updateLEDs(int number) {
  int unitsDigit = number % 10;
  digitalWrite(LED1_PIN, (unitsDigit >= 0 && unitsDigit <= 2) ? HIGH : LOW);
  digitalWrite(LED2_PIN, (unitsDigit >= 3 && unitsDigit <= 5) ? HIGH : LOW);
  digitalWrite(LED3_PIN, (unitsDigit >= 6 && unitsDigit <= 9) ? HIGH : LOW);
}
void updateServos(int number) {
  int tensDigit = number / 10;
  if (tensDigit >= 0 && tensDigit <= 2) {
    servo1.write(30);
    servo2.write(60);
    servo3.write(90);
  } else if (tensDigit >= 3 && tensDigit <= 5) {
    servo1.write(60);
    servo2.write(90);
    servo3.write(120);
  } else if (tensDigit >= 6 && tensDigit <= 9) {
    servo1.write(90);
    servo2.write(120);
    servo3.write(150);
  }
}

