  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000  
};
int currentNumber = 0;
unsigned long previousMillisLED = 0;
unsigned long previousMillisDisplay = 0;
unsigned long previousMillisServo = 0;
bool isServoPaused = false;
unsigned long pauseStartTime = 0;
bool led1State = LOW;
bool led2State = HIGH;
void setup() {
