      static int pos1 = 0;
      static int pos2 = 180;
      static int dir1 = 1;
      static int dir2 = -1;
      pos1 += dir1;
      pos2 += dir2;
      if (pos1 >= 180 || pos1 <= 0) dir1 = -dir1;
      if (pos2 >= 180 || pos2 <= 0) {
        dir2 = -dir2;
        isServoPaused = true;
        pauseStartTime = currentMillis;
      }
      servo1.write(pos1);
      servo2.write(pos2);
    }
  }
  else if (currentMillis - pauseStartTime >= 2000) {
    isServoPaused = false;
  }
}
