int sweepDirection = -1;
void setup() {
  for (int i = 0; i < 3; i++) {
    pinMode(ledPins[i], OUTPUT);
    pinMode(rgbPins[i], OUTPUT);
  }
  servo1.attach(servoPins[0]);
