void sweepServos() {
  if (servoPos == 0 || servoPos == 180) {
    sweepDirection *= -1;
    delay(2000);
  }
  if (sweepDirection == 1 )
  {    while(servoPos < 180)
    {servoPos += sweepDirection * 10;
    servo1.write(servoPos);
    servo2.write(servoPos);
    delay(100);}}
  if (sweepDirection == -1 )
  {  while(servoPos > 0 )
    {servoPos += sweepDirection * 10;
    servo1.write(servoPos);
    servo2.write(servoPos);
    delay(100);}}
}

