void setup() {
  pinMode(LED_PIN, OUTPUT);
  pinMode(RGB1_R, OUTPUT); pinMode(RGB1_G, OUTPUT); pinMode(RGB1_B, OUTPUT);
  pinMode(RGB2_R, OUTPUT); pinMode(RGB2_G, OUTPUT); pinMode(RGB2_B, OUTPUT);
  pinMode(RGB3_R, OUTPUT); pinMode(RGB3_G, OUTPUT); pinMode(RGB3_B, OUTPUT);
  for (int i = 0; i < 4; i++) pinMode(BAR_PINS[i], OUTPUT);
}
void loop() {
  unsigned long currentMillis = millis();
  int stage = (currentMillis / 2000) % 4;
  static unsigned long lastBlink = 0;
  static bool ledState = LOW;
  if (currentMillis - lastBlink >= 2000) {
    lastBlink = currentMillis;
    ledState = !ledState;
    digitalWrite(LED_PIN, ledState);
  }
  switch (stage) {
    case 0: 
      setRGB(RGB1_R, RGB1_G, RGB1_B, HIGH, LOW, LOW);
      setRGB(RGB2_R, RGB2_G, RGB2_B, LOW, LOW, LOW);
      setRGB(RGB3_R, RGB3_G, RGB3_B, LOW, LOW, LOW);
      break;
    case 1: 
      setRGB(RGB1_R, RGB1_G, RGB1_B, LOW, HIGH, LOW);
      setRGB(RGB2_R, RGB2_G, RGB2_B, LOW, LOW, HIGH);
      setRGB(RGB3_R, RGB3_G, RGB3_B, LOW, LOW, LOW);
      break;
    case 2: 
      setRGB(RGB1_R, RGB1_G, RGB1_B, LOW, LOW, HIGH);
      setRGB(RGB2_R, RGB2_G, RGB2_B, HIGH, LOW, LOW);
      setRGB(RGB3_R, RGB3_G, RGB3_B, LOW, HIGH, LOW);
      break;
    case 3: 
      setRGB(RGB1_R, RGB1_G, RGB1_B, HIGH, HIGH, HIGH);
      setRGB(RGB2_R, RGB2_G, RGB2_B, HIGH, HIGH, HIGH);
      setRGB(RGB3_R, RGB3_G, RGB3_B, HIGH, HIGH, HIGH);
      break;
  }
  for (int i = 0; i < 4; i++) {
    digitalWrite(BAR_PINS[i], (i <= stage) ? HIGH : LOW);
  }
}
