const int SHCP_PIN = A2;  
const int STCP_PIN = A1;  
const int DS_PIN = A0;    
const int LED1_PIN = 2;   
const int LED2_PIN = 3;   
const int LED3_PIN = 4;   
const int RGB1_R_PIN = 5; 
const int RGB1_G_PIN = 6; 
const int RGB1_B_PIN = 7; 
const int RGB2_R_PIN = 8; 
const int RGB2_G_PIN = 9; 
const int RGB2_B_PIN = 10; 
const byte segmentCodes[] = {
  0b11000000, 
  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000, 
};
int countdown = 9;
int ledState = 0;
int rgbColor = 0;
void setup() {
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  pinMode(LED1_PIN, OUTPUT);
  pinMode(LED2_PIN, OUTPUT);
  pinMode(LED3_PIN, OUTPUT);
  pinMode(RGB1_R_PIN, OUTPUT);
  pinMode(RGB1_G_PIN, OUTPUT);
  pinMode(RGB1_B_PIN, OUTPUT);
  pinMode(RGB2_R_PIN, OUTPUT);
  pinMode(RGB2_G_PIN, OUTPUT);
  pinMode(RGB2_B_PIN, OUTPUT);
  displayNumber(99);
}
void loop() {
  displayNumber(countdown * 11); 
  updateLEDs();
  updateRGBs();
  delay(2000);
  countdown--;
  if (countdown == 0) {
    countdown = 9;
    digitalWrite(LED1_PIN, LOW);
    digitalWrite(LED2_PIN, LOW);
    digitalWrite(LED3_PIN, LOW);
    analogWrite(RGB1_R_PIN, 255);
    analogWrite(RGB1_G_PIN, 255);
    analogWrite(RGB1_B_PIN, 255);
    analogWrite(RGB2_R_PIN, 255);
    analogWrite(RGB2_G_PIN, 255);
    analogWrite(RGB2_B_PIN, 255);
    displayNumber(0); 
    delay(2000);
  }
}
void displayNumber(int number) {
  int tens = number / 10;
  int ones = number % 10;
  byte tensCode = segmentCodes[tens];
  byte onesCode = segmentCodes[ones];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, onesCode);
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, tensCode);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}
void updateLEDs() {
  digitalWrite(LED1_PIN, ledState == 0 ? HIGH : LOW);
  digitalWrite(LED2_PIN, ledState == 1 ? HIGH : LOW);
  digitalWrite(LED3_PIN, ledState == 2 ? HIGH : LOW);
  ledState = (ledState + 1) % 3;
}
void updateRGBs() {
  switch (rgbColor) {
    case 0:
      analogWrite(RGB1_R_PIN, 255);
      analogWrite(RGB1_G_PIN, 0);
      analogWrite(RGB1_B_PIN, 0);
      analogWrite(RGB2_R_PIN, 255);
      analogWrite(RGB2_G_PIN, 0);
      analogWrite(RGB2_B_PIN, 0);
      break;
    case 1:
      analogWrite(RGB1_R_PIN, 0);
      analogWrite(RGB1_G_PIN, 255);
      analogWrite(RGB1_B_PIN, 0);
      analogWrite(RGB2_R_PIN, 0);
      analogWrite(RGB2_G_PIN, 255);
      analogWrite(RGB2_B_PIN, 0);
      break;
    case 2:
      analogWrite(RGB1_R_PIN, 0);
      analogWrite(RGB1_G_PIN, 0);
      analogWrite(RGB1_B_PIN, 255);
      analogWrite(RGB2_R_PIN, 0);
      analogWrite(RGB2_G_PIN, 0);
      analogWrite(RGB2_B_PIN, 255);
      break;
  }
  rgbColor = (rgbColor + 1) % 3;
}

const int LED_PIN = 2;          
const int RGB_RED_PIN = 3;      
const int RGB_GREEN_PIN = 4;    
const int RGB_BLUE_PIN = 5;     
const int SHCP_PIN = A2;        
const int STCP_PIN = A1;        
const int DS_PIN = A0;          
const byte segmentCodes[] = {
  0b11000000, 
  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000  
};
int currentNumber = 0;          
unsigned long lastLedUpdate = 0;
unsigned long lastRgbUpdate = 0;
unsigned long lastDisplayUpdate = 0;
int rgbState = 0;               
void setup() {
  pinMode(LED_PIN, OUTPUT);
  pinMode(RGB_RED_PIN, OUTPUT);
  pinMode(RGB_GREEN_PIN, OUTPUT);
  pinMode(RGB_BLUE_PIN, OUTPUT);
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  displayNumber(0); 
  setRgbColor(rgbState);
  rgbState = (rgbState + 1) % 7;
  lastRgbUpdate = millis();
}
void loop() {
  unsigned long currentTime = millis();
  if (currentTime - lastLedUpdate >= 2000) {
    digitalWrite(LED_PIN, !digitalRead(LED_PIN));
    lastLedUpdate = currentTime;
  }
  if (currentTime - lastRgbUpdate >= 2000) {
    setRgbColor(rgbState);
    rgbState = (rgbState + 1) % 7;
    lastRgbUpdate = currentTime;
  }
  if (currentTime - lastDisplayUpdate >= 2000) {
    currentNumber = (currentNumber + 1) % 100;
    displayNumber(currentNumber);
    lastDisplayUpdate = currentTime;
  }
}
void setRgbColor(int state) {
  switch (state) {
    case 0: 
      digitalWrite(RGB_RED_PIN, HIGH);
      digitalWrite(RGB_GREEN_PIN, LOW);
      digitalWrite(RGB_BLUE_PIN, LOW);
      break;
    case 1: 
      digitalWrite(RGB_RED_PIN, LOW);
      digitalWrite(RGB_GREEN_PIN, HIGH);
      digitalWrite(RGB_BLUE_PIN, LOW);
      break;
    case 2: 
      digitalWrite(RGB_RED_PIN, LOW);
      digitalWrite(RGB_GREEN_PIN, LOW);
      digitalWrite(RGB_BLUE_PIN, HIGH);
      break;
    case 3: 
      digitalWrite(RGB_RED_PIN, HIGH);
      digitalWrite(RGB_GREEN_PIN, HIGH);
      digitalWrite(RGB_BLUE_PIN, LOW);
      break;
    case 4: 
      digitalWrite(RGB_RED_PIN, LOW);
      digitalWrite(RGB_GREEN_PIN, HIGH);
      digitalWrite(RGB_BLUE_PIN, HIGH);
      break;
    case 5: 
      digitalWrite(RGB_RED_PIN, HIGH);
      digitalWrite(RGB_GREEN_PIN, LOW);
      digitalWrite(RGB_BLUE_PIN, HIGH);
      break;
    case 6: 
      digitalWrite(RGB_RED_PIN, HIGH);
      digitalWrite(RGB_GREEN_PIN, HIGH);
      digitalWrite(RGB_BLUE_PIN, HIGH);
      break;
  }
}
void displayNumber(int number) {
  int tens = number / 10;
  int ones = number % 10;
  byte tensCode = segmentCodes[tens];
  byte onesCode = segmentCodes[ones];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, onesCode);
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, tensCode);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}

const int LED1_PIN = 2;  
const int LED2_PIN = 3;  
const int BTN1_PIN = 4;  
const int BTN2_PIN = 5;  
const int BTN3_PIN = 6;  
const unsigned long DEBOUNCE_DELAY = 150;  
bool led1State = LOW;  
bool led2State = LOW;  
unsigned long lastDebounceTimeBtn1 = 0;  
unsigned long lastDebounceTimeBtn2 = 0;  
unsigned long lastDebounceTimeBtn3 = 0;  
void setup() {
  pinMode(LED1_PIN, OUTPUT);
  pinMode(LED2_PIN, OUTPUT);
  pinMode(BTN1_PIN, INPUT_PULLUP);
  pinMode(BTN2_PIN, INPUT_PULLUP);
  pinMode(BTN3_PIN, INPUT_PULLUP);
  digitalWrite(LED1_PIN, led1State);
  digitalWrite(LED2_PIN, led2State);
}
void loop() {
  handleButton(BTN1_PIN, lastDebounceTimeBtn1, led1State, LED1_PIN);
  handleButton(BTN2_PIN, lastDebounceTimeBtn2, led2State, LED2_PIN);
  handleResetButton(BTN3_PIN, lastDebounceTimeBtn3);
}
void handleButton(int buttonPin, unsigned long &lastDebounceTime, bool &ledState, int ledPin) {
  if (digitalRead(buttonPin) == LOW) {
    if (millis() - lastDebounceTime > DEBOUNCE_DELAY) {
      ledState = !ledState;
      digitalWrite(ledPin, ledState);
      lastDebounceTime = millis();
      delay(2000);  
    }
  }
}
void handleResetButton(int buttonPin, unsigned long &lastDebounceTime) {
  if (digitalRead(buttonPin) == LOW) {
    if (millis() - lastDebounceTime > DEBOUNCE_DELAY) {
      led1State = LOW;
      led2State = LOW;
      digitalWrite(LED1_PIN, led1State);
      digitalWrite(LED2_PIN, led2State);
      lastDebounceTime = millis();
      delay(2000);  
    }
  }
}

#include <Servo.h>
Servo servo1;
const int LED_PIN = 2;
const int BTN1_PIN = 3;
const int BTN2_PIN = 4;
const int SERVO_PIN = 5;
int currentAngle = 90;
void setup() {
  pinMode(LED_PIN, OUTPUT);
  pinMode(BTN1_PIN, INPUT_PULLUP);
  pinMode(BTN2_PIN, INPUT_PULLUP);
  servo1.attach(SERVO_PIN);
  servo1.write(currentAngle);
  digitalWrite(LED_PIN, LOW);
}
void loop() {
  if (digitalRead(BTN1_PIN) == LOW) {
    delay(150); 
    if (digitalRead(BTN1_PIN) == LOW) {
      if (currentAngle < 180) {
        currentAngle += 30;
        if (currentAngle > 180) currentAngle = 180;
        digitalWrite(LED_PIN, HIGH);
        servo1.write(currentAngle);
        delay(2000); 
        digitalWrite(LED_PIN, LOW);
      }
      while (digitalRead(BTN1_PIN) == LOW); 
    }
  }
  if (digitalRead(BTN2_PIN) == LOW) {
    delay(150); 
    if (digitalRead(BTN2_PIN) == LOW) {
      if (currentAngle > 0) {
        currentAngle -= 30;
        if (currentAngle < 0) currentAngle = 0;
        digitalWrite(LED_PIN, HIGH);
        servo1.write(currentAngle);
        delay(2000); 
        digitalWrite(LED_PIN, LOW);
      }
      while (digitalRead(BTN2_PIN) == LOW); 
    }
  }
}

#include <Servo.h>
Servo servo1;
Servo servo2;
const int LED_PIN = 2;
const int BTN1_PIN = 3;
const int BTN2_PIN = 4;
const int SERVO1_PIN = 5;
const int SERVO2_PIN = 6;
unsigned long lastStateChangeTime = 0; 
bool isBlinking = false; 
bool btn1Active = false; 
bool btn2Active = false; 
bool led_date = LOW;
void setup() {
  pinMode(LED_PIN, OUTPUT);
  pinMode(BTN1_PIN, INPUT_PULLUP);
  pinMode(BTN2_PIN, INPUT_PULLUP);
  servo1.attach(SERVO1_PIN);
  servo2.attach(SERVO2_PIN);
  servo1.write(0);
  servo2.write(0);
  digitalWrite(LED_PIN, LOW);
}
void loop() {
  bool btn1Pressed = digitalRead(BTN1_PIN) == LOW;
  bool btn2Pressed = digitalRead(BTN2_PIN) == LOW;
  if (btn1Pressed && !btn2Active && !btn1Active) {
    btn1Active = true;
    btn2Active = false; 
    isBlinking = false;
    servo1.write(90);
    led_date = HIGH;
    digitalWrite(LED_PIN, led_date);
    lastStateChangeTime = millis();
    delay(2000);
  } else if (btn2Pressed && !btn1Active && !btn2Active) {
    btn2Active = true;
    btn1Active = false; 
    isBlinking = false;
    servo2.write(180);
    led_date = LOW;
    digitalWrite(LED_PIN, led_date);
    lastStateChangeTime = millis();
    delay(2000);
  }
  if ((btn1Active && btn2Pressed) || (btn2Active && btn1Pressed)) {
    servo1.write(0);
    servo2.write(0);
    isBlinking = true;
    lastStateChangeTime = millis();
  }
  if (isBlinking) {
    blinkLED();
  }
  else{
    digitalWrite(LED_PIN, led_date);
  }
}
void blinkLED() {
  unsigned long currentTime = millis();
  static unsigned long lastBlinkTime = 0;
  if (currentTime - lastBlinkTime >= 2000) {
    digitalWrite(LED_PIN, !digitalRead(LED_PIN));
    lastBlinkTime = currentTime;
  }
}

const int LED_PIN = 2;          
const int BTN1_PIN = 3;         
const int BTN2_PIN = 4;         
const int POT1_PIN = A3;        
const int POT2_PIN = A4;        
const int POT3_PIN = A5;        
const unsigned long DEBOUNCE_DELAY = 150;  
bool manualMode = true;         
unsigned long lastDebounceTime = 0;  
int ledBrightness = 0;          
int minBrightness = 0;          
int maxBrightness = 0;          
void setup() {
  pinMode(LED_PIN, OUTPUT);
  pinMode(BTN1_PIN, INPUT_PULLUP);
  pinMode(BTN2_PIN, INPUT_PULLUP);
  analogWrite(LED_PIN, 0);  
}
void loop() {
  handleButtons();  
  if (manualMode) {
    manualModeOperation();
  } else {
    autoModeOperation();
  }
}
void handleButtons() {
  unsigned long currentTime = millis();
  if (currentTime - lastDebounceTime > DEBOUNCE_DELAY) {
    if (digitalRead(BTN1_PIN) == LOW) {
      manualMode = false;  
      lastDebounceTime = currentTime;
    } else if (digitalRead(BTN2_PIN) == LOW) {
      manualMode = true;   
      lastDebounceTime = currentTime;
    }
  }
}
void manualModeOperation() {
  int pot1Value = analogRead(POT1_PIN);
  ledBrightness = map(pot1Value, 0, 1023, 0, 255);
  analogWrite(LED_PIN, ledBrightness);
}
void autoModeOperation() {
  minBrightness = map(analogRead(POT2_PIN), 0, 1023, 0, 255);
  maxBrightness = map(analogRead(POT3_PIN), 0, 1023, 0, 255);
  for (int i = minBrightness; i <= maxBrightness; i++) {
    analogWrite(LED_PIN, i);
    delay(20);  
  }
  for (int i = maxBrightness; i >= minBrightness; i--) {
    analogWrite(LED_PIN, i);
    delay(20);  
  }
}

