const int BUTTON_PINS[] = {5, 6, 7};
const int LED_PINS[] = {2, 3, 4};
const int BARGRAPH_PINS[] = {8, 9, 10, 11, 12, 13, 44, 45};
bool ledStates[] = {LOW, LOW, LOW};
int lastButtonStates[] = {HIGH, HIGH, HIGH};
unsigned long lastDebounceTime[] = {0, 0, 0};
const long DEBOUNCE_DELAY = 150;
void setup() {
  for (int i = 0; i < 3; i++) {
    pinMode(BUTTON_PINS[i], INPUT_PULLUP);
    pinMode(LED_PINS[i], OUTPUT);
    digitalWrite(LED_PINS[i], LOW);
  }
  for (int i = 0; i < 8; i++) {
    pinMode(BARGRAPH_PINS[i], OUTPUT);
    digitalWrite(BARGRAPH_PINS[i], LOW);
  }
}
void loop() {
  for (int i = 0; i < 3; i++) {
    if (digitalRead(BUTTON_PINS[i]) == LOW) {
      delay(20);  
      if (digitalRead(BUTTON_PINS[i]) == LOW) {
        ledStates[i] = !ledStates[i];
        digitalWrite(LED_PINS[i], ledStates[i]);
        updateBargraph();
        while (digitalRead(BUTTON_PINS[i]) == LOW) {
          delay(10);  
        }
        delay(20);  
      }
    }
  }
}
void updateBargraph() {
  int value = 0;
  for (int i = 0; i < 3; i++) {
    if (ledStates[i]) {
      value += (1 << i);
    }
  }
  value -= 1;
  for (int i = 0; i < 8; i++) {
    digitalWrite(BARGRAPH_PINS[i], (i == value) ? HIGH : LOW);
  }
}

const int BUTTON_PIN = 4;  
int currentCount = 0;
int overflowCount = 0;
void setup() {
  for (int pin = 5; pin <= 13; pin++) {  
    pinMode(pin, OUTPUT);
    digitalWrite(pin, LOW);
  }
  pinMode(44, OUTPUT);  
  digitalWrite(44, LOW);
  pinMode(BUTTON_PIN, INPUT_PULLUP);
}
void updateDisplay() {
  for (int seg = 5; seg <= 13; seg++) {
    digitalWrite(seg, (seg - 5) < currentCount ? HIGH : LOW);
  }
  digitalWrite(44, (9 < currentCount) ? HIGH : LOW);  
  digitalWrite(2, overflowCount & 0b01);  
  digitalWrite(3, overflowCount & 0b10);  
}
void loop() {
  if (digitalRead(BUTTON_PIN) == LOW) {
    delay(150); 
    if (digitalRead(BUTTON_PIN) == LOW) {
      currentCount++;
      if (currentCount > 10) {
        currentCount = 0;
        overflowCount = (overflowCount + 1) % 4;
      }
      updateDisplay();
      while (digitalRead(BUTTON_PIN) == LOW);
    }
  }
}

const int SHCP_PIN = A2;  
const int STCP_PIN = A1;  
const int DS_PIN = A0;    
const int BUTTON1_PIN = 3;  
const int BUTTON2_PIN = 4;  
const int LED_PIN = 2;      
const byte segmentCodes[] = {
  0b11000000, 
  0b11111001, 
  0b10100100, 
  0b10110000, 
  0b10011001, 
  0b10010010, 
  0b10000010, 
  0b11111000, 
  0b10000000, 
  0b10010000, 
};
int currentNumber = 0;
bool lastButton1State = HIGH;
bool lastButton2State = HIGH;
unsigned long lastDebounceTime1 = 0;
unsigned long lastDebounceTime2 = 0;
const unsigned long debounceDelay = 150; 
bool lastStableButton1State = HIGH;
bool lastStableButton2State = HIGH;
void setup() {
  pinMode(SHCP_PIN, OUTPUT);
  pinMode(STCP_PIN, OUTPUT);
  pinMode(DS_PIN, OUTPUT);
  pinMode(BUTTON1_PIN, INPUT_PULLUP);
  pinMode(BUTTON2_PIN, INPUT_PULLUP);
  pinMode(LED_PIN, OUTPUT);
  displayNumber(currentNumber);
  updateLED();
}
void loop() {
  bool button1State = digitalRead(BUTTON1_PIN);
  if (button1State != lastButton1State) {
    lastDebounceTime1 = millis(); 
  }
  if ((millis() - lastDebounceTime1) > debounceDelay) {
    if (button1State == LOW && lastStableButton1State == HIGH) {
      currentNumber = (currentNumber + 1) % 10; 
      displayNumber(currentNumber);
      updateLED();
    }
    lastStableButton1State = button1State;
  }
  lastButton1State = button1State; 
  bool button2State = digitalRead(BUTTON2_PIN);
  if (button2State != lastButton2State) {
    lastDebounceTime2 = millis();
  }
  if ((millis() - lastDebounceTime2) > debounceDelay) {
    if (button2State == LOW && lastStableButton2State == HIGH) {
      currentNumber = (currentNumber - 1 + 10) % 10; 
      displayNumber(currentNumber);
      updateLED();
    }
    lastStableButton2State = button2State;
  }
  lastButton2State = button2State; 
}
void displayNumber(int number) {
  byte code = segmentCodes[number];
  shiftOut(DS_PIN, SHCP_PIN, MSBFIRST, code);
  digitalWrite(STCP_PIN, HIGH);
  digitalWrite(STCP_PIN, LOW);
}
void updateLED() {
  if (currentNumber % 2 == 0) {
    digitalWrite(LED_PIN, HIGH); 
  } else {
    digitalWrite(LED_PIN, LOW);  
  }
}

const int LED1_PIN = 2;  
const int LED2_PIN = 3;  
const int LED3_PIN = 4;  
const int BUTTON_PIN = 5;  
const int RGB1_R_PIN = 6;  
const int RGB1_G_PIN = 7;  
const int RGB1_B_PIN = 8;  
const int RGB2_R_PIN = 9;  
const int RGB2_G_PIN = 10;  
const int RGB2_B_PIN = 11;  
const int RGB3_R_PIN = 12;  
const int RGB3_G_PIN = 13;  
const int RGB3_B_PIN = 44;  
int mode = 0;
unsigned long lastModeChange = 0;
bool buttonPressed = false;
void setup() {
  pinMode(LED1_PIN, OUTPUT);
  pinMode(LED2_PIN, OUTPUT);
  pinMode(LED3_PIN, OUTPUT);
  pinMode(BUTTON_PIN, INPUT_PULLUP);
  pinMode(RGB1_R_PIN, OUTPUT);
  pinMode(RGB1_G_PIN, OUTPUT);
  pinMode(RGB1_B_PIN, OUTPUT);
  pinMode(RGB2_R_PIN, OUTPUT);
  pinMode(RGB2_G_PIN, OUTPUT);
  pinMode(RGB2_B_PIN, OUTPUT);
  pinMode(RGB3_R_PIN, OUTPUT);
  pinMode(RGB3_G_PIN, OUTPUT);
  pinMode(RGB3_B_PIN, OUTPUT);
}
void loop() {
  if (digitalRead(BUTTON_PIN) == LOW && millis() - lastModeChange > 2000) {
    mode = (mode + 1) % 4;
    lastModeChange = millis();
    updateLEDs();
    delay(200); 
  }
  switch (mode) {
    case 0:
      setRGBColor(RGB1_R_PIN, RGB1_G_PIN, RGB1_B_PIN, 255, 0, 0); 
      setRGBColor(RGB2_R_PIN, RGB2_G_PIN, RGB2_B_PIN, 0, 255, 0); 
      setRGBColor(RGB3_R_PIN, RGB3_G_PIN, RGB3_B_PIN, 0, 0, 255); 
      digitalWrite(LED1_PIN, HIGH); 
      digitalWrite(LED2_PIN, LOW);
      digitalWrite(LED3_PIN, LOW);
      break;
    case 1:
      setRGBColor(RGB1_R_PIN, RGB1_G_PIN, RGB1_B_PIN, 255, 255, 0); 
      setRGBColor(RGB2_R_PIN, RGB2_G_PIN, RGB2_B_PIN, 0, 255, 255); 
      setRGBColor(RGB3_R_PIN, RGB3_G_PIN, RGB3_B_PIN, 255, 0, 255); 
      digitalWrite(LED1_PIN, LOW);
      digitalWrite(LED2_PIN, HIGH); 
      digitalWrite(LED3_PIN, LOW);
      break;
    case 2:
      setRGBColor(RGB1_R_PIN, RGB1_G_PIN, RGB1_B_PIN, 255, 200, 150); 
      setRGBColor(RGB2_R_PIN, RGB2_G_PIN, RGB2_B_PIN, 255, 200, 150);
      setRGBColor(RGB3_R_PIN, RGB3_G_PIN, RGB3_B_PIN, 255, 200, 150);
      digitalWrite(LED1_PIN, LOW);
      digitalWrite(LED2_PIN, LOW);
      digitalWrite(LED3_PIN, HIGH); 
      break;
    case 3:
      setRGBColor(RGB1_R_PIN, RGB1_G_PIN, RGB1_B_PIN, 0, 0, 0); 
      setRGBColor(RGB2_R_PIN, RGB2_G_PIN, RGB2_B_PIN, 0, 0, 0);
      setRGBColor(RGB3_R_PIN, RGB3_G_PIN, RGB3_B_PIN, 0, 0, 0);
      digitalWrite(LED1_PIN, LOW);
      digitalWrite(LED2_PIN, LOW);
      digitalWrite(LED3_PIN, LOW); 
      break;
  }
}
void setRGBColor(int rPin, int gPin, int bPin, int r, int g, int b) {
  analogWrite(rPin, r);
  analogWrite(gPin, g);
  analogWrite(bPin, b);
}
void updateLEDs() {
  digitalWrite(LED1_PIN, LOW);
  digitalWrite(LED2_PIN, LOW);
  digitalWrite(LED3_PIN, LOW);
  setRGBColor(RGB1_R_PIN, RGB1_G_PIN, RGB1_B_PIN, 0, 0, 0);
  setRGBColor(RGB2_R_PIN, RGB2_G_PIN, RGB2_B_PIN, 0, 0, 0);
  setRGBColor(RGB3_R_PIN, RGB3_G_PIN, RGB3_B_PIN, 0, 0, 0);
}
